export function SortData(data) {
  const lines = data.split('\n');
	// console.log(lines);

  const values = lines.map((line) => {
		return line.split(',');
	});

	values.sort((a, b) => {
		if (a[3] === b[3]) {
			return a[4] - b[4];
		} else {
			return a[3] - b[3];
		}
	});

	// Regroupe les tableaux triés par coordonnée x en un tableau principal
	const groupedValues = [];
	let currentX = null;

	for (let i = 0; i < values.length; i++) {
		const value = values[i];
		const x = value[3];

		if (x !== currentX) {
			currentX = x;
			groupedValues.push([]);
		}

		groupedValues[groupedValues.length - 1].push(value);
	}

	return groupedValues
}
