import React, { useState, useEffect } from 'react';
import { FormatData } from './components/formatData.js';
import { FormatToCSV } from './components/formatToCSV.js';
import { ReadFile } from './components/readFile.js';

function App() {
  // Appel de la fonction pour lire le fichier local
  const fileName = './planets.txt';
  const file = ReadFile(fileName);

  // Appel de la fonction pour formatter le fichier au format CSV
  const csvFile = FormatToCSV(file);

  // Tri des données
  const [formattedData, setFormattedData] = useState(null);

  useEffect(() => {
    FormatData(csvFile)
      .then((grid) => {
        setFormattedData(grid);
      })
      .catch((err) => {
        console.error(err);
      });
  }, [csvFile]);

  console.log(formattedData);

  return (
    <div className="grid-container">
      {formattedData.map((planet) => (
        <div
          className="planet"
          key={planet.id}
          style={{
            gridColumnStart: planet.x + 1,
            gridColumnEnd: planet.x + 2,
            gridRowStart: planet.y + 1,
            gridRowEnd: planet.y + 2,
          }}
        >
          {planet.name}
        </div>
      ))}
    </div>
  );
}

export default App;
