	dataToFormat.splice(0, 1, 'id,name,govsystem,x,y,race,prod,activity,tag,civlevel,planetsize,SC');

  dataToFormat.map((line) => {
    const lineToFormat = line.split(',');
    lineToFormat.slice(0, -2);
    return lineToFormat.join(',');
