import { FormatData } from './components/formatData';
import { FormatToCSV } from './components/formatToCSV';
import { MaxCoord } from './components/maxCoord';
import { ReadFile } from './components/readFile';
import { RenderGrid } from './components/renderGrid';

function App() {
  // Appel de la fonction pour lire le fichier local
  const fileName = './planetsFull.txt';
  const file = ReadFile(fileName);
  // Appel de la fonction pour formatter le fichier au format CSV
  const csvFile = FormatToCSV(file);
  // Appel de la fonction qui structure les données
  const data = FormatData(csvFile);
  // Appel de la fonction pour avoir la coordonnée maximale
  const maxCoord = MaxCoord(csvFile);
  // Génération de la grille
  const grid = RenderGrid(data, maxCoord);

  // Ajout de l'écouteur d'événement pour la molette de la souris
  const container = document.getElementById("container");

  container.addEventListener("wheel", function(event) {
    var delta = event.deltaY;

    if (delta > 0) {
      // Zoom arrière (dézoom)
      table.style.transform = "scale(0.9)";
    } else if (delta < 0) {
      // Zoom avant (zoom)
      table.style.transform = "scale(1.1)";
    }

    // Ajustement de la position du tableau en fonction du niveau de zoom
    var table = document.getElementById("myTable");
    var containerWidth = container.offsetWidth;
    var containerHeight = container.offsetHeight;
    var tableWidth = table.offsetWidth;
    var tableHeight = table.offsetHeight;
    var scale = parseFloat(table.style.transform.replace("scale(", "").replace(")", ""));
    var translateX = (containerWidth - (tableWidth * scale)) / 2;
    var translateY = (containerHeight - (tableHeight * scale)) / 2;
    table.style.transform += " translateX(" + translateX + "px) translateY(" + translateY + "px)";
  });

  return(
    <div id="container">
      <table id="myTable" style={{width: "5000px", height: "5000px"}}>
        {grid}
      </table>
    </div>
  )
}

export default App;
