import { useState, useEffect } from "react";
import Papa from 'papaparse';

export function FormatData(csvFile) {
  const [grid, setGrid] = useState({});

	useEffect(() => {
		const planets = [];

		csv(csvFile)
			.on('data', (row) => {
				planets.push({
					id: parseInt(row.id),
          name: row.name,
          x: parseInt(row.x),
          y: parseInt(row.y),
          govsystem: parseInt(row.govsystem),
          race: parseInt(row.race),
          prod: parseInt(row.prod),
          activity: parseInt(row.activity),
          tag: row.tag,
          civlevel: parseInt(row.civlevel)
				})
			})
			.on('end', () => {
				planets.sort((a, b) => {
          if (a.x === b.x) {
            return a.y - b.y;
          }
          return a.x - b.x;
        });

        const grid = {};
        planets.forEach((planet) => {
          if (!grid[planet.x]) {
            grid[planet.x] = {};
          }
          if (!grid[planet.x][planet.y]) {
            grid[planet.x][planet.y] = [];
          }
          grid[planet.x][planet.y].push(planet);
        });

        // Mise à jour de l'état de la grille de planètes triées
        setGrid(grid);
      });
  }, [csvFile]);

  return grid;
}

