import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';

function App() {
  const fileName = './planetsMinimal.txt';
  const [file, setFileContent] = useState('');
  useEffect(() => {
    fetch(fileName)
      .then(response => response.text())
      .then(data => setFileContent(data))
      .catch(error => console.log(error));
  }, []);

  const [grid, setGrid] = useState(); // Ajout d'une variable d'état pour contenir grid

  const formatData = (fileContent) => {
    const dataToFormat = fileContent.split('\n');
    dataToFormat.splice(0, 1);
    dataToFormat.splice(0, 1, 'id,name,govsystem,x,y,race,prod,activity,tag,civlevel,planetsize,SC');
    const csvFile = dataToFormat.join('\n').replace(/ /g, ',');
    const lines = csvFile.split('\n');
    const newLines = lines.map((line) => {
      const values = line.split(',');
      const newValues = values.slice(0, -2);
      return newValues.join(',');
    });
    const newFileContent = newLines.join('\n');

    const planets = [];
    Papa.parse(newFileContent, {
      header: true,
      step: (row) => {
        const planet = {
          id: parseInt(row.data.id),
          name: row.data.name,
          x: parseInt(row.data.x),
          y: parseInt(row.data.y),
          govsystem: parseInt(row.data.govsystem),
          race: parseInt(row.data.race),
          prod: parseInt(row.data.prod),
          activity: parseInt(row.data.activity),
          tag: row.data.tag,
          civlevel: parseInt(row.data.civlevel)
        };
        planets.push(planet);
      },
      complete: (result) => {
        planets.sort((a, b) => {
          if (a.x === b.x) {
            return a.y - b.y;
          }
          return a.x - b.x;
        });

        const grid = {};
        planets.forEach((planet) => {
          if (!grid[planet.x]) {
            grid[planet.x] = {};
          }
          if (!grid[planet.x][planet.y]) {
            grid[planet.x][planet.y] = [];
          }
          grid[planet.x][planet.y].push(planet);
        });

        setGrid(grid); // Mise à jour de la variable d'état pour contenir grid
      }
    });
  };

  useEffect(() => {
    if (file) {
      formatData(file);
    }
  }, [file]);

  console.log(grid["19"]["7"]["0"]["name"]); // Affichage de la variable d'état

  return(
    <div>
      <p>Bla</p>
    </div>
  );
}

export default App;

