import React, { useState, useEffect } from 'react';
import { FormatData } from './components/formatData';
import { FormatToCSV } from './components/formatToCSV';
import { ReadFile } from './components/readFile';
import { RenderGrid } from './components/renderGrid';

function App() {
  // Appel de la fonction pour lire le fichier local
  const fileName = './planets.txt';
  const file = ReadFile(fileName);

  // Appel de la fonction pour formatter le fichier au format CSV
  const csvFile = FormatToCSV(file);

  // Appel de la fonction qui trie et structure les données
  const [formattedData, setFormattedData] = useState(null);
  useEffect(() => {
    FormatData(csvFile)
      .then((structuredData) => {
        setFormattedData(structuredData);
      })
      .catch((err) => {
        console.error(err);
      });
  }, [csvFile]);
  console.log(formattedData);

  // Appel de la fonction qui affiche les données dans le navigateur sous la forme d'une grille

}

export default App;
