export function SortData(data) {
	const records = data.split('\n').map((line) => line.split(','));
	const sortedPlanets = records.slice(1).sort((a, b) => {
		if (a[3] === b[3]) {
			return a[4] - b[4];
		}
		return a[3] - b[3];
	});
	return sortedPlanets;
}

